import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/core/auth.service';
import { Role } from 'src/app/shared/enums';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {

  constructor(private authService: AuthService) { }

  ngOnInit(): void {
  }

  get isAdmin(): boolean {
    return this.userRole === this.authService.getUserRoles();
  }
  get userLoggedIn() {
    return this.authService.isLoggedIn();
  }

  get isEditorOrAdminOrViewer(): boolean {
    return [Role.Admin, Role.Editor, Role.Viewer].includes(this.userRole as Role);
  }
  get userName() {
    return this.authService.getUserRoles();
  }
  logout() {
    this.authService.logout(); // Clear token, etc.
  }
}
<header class="header">
  <div class="logo">{{userName ?? 'RBAC System'}}</div>
  <nav *ngIf="userLoggedIn" class="nav-links">
    <!-- Show Users tab only for Admin -->
    <a *ngIf="isAdmin" routerLink="/users">Users</a>

    <!-- Show Content tab for Admin, Editor, Viewer -->
    <a *ngIf="isAdmin" routerLink="/content">Content</a>

    <a routerLink="/auth/login" (click)="logout()" class="logout-btn">Logout</a>
  </nav>
</header>
